import React, { useEffect, useState, useMemo } from "react";
import axios from 'axios'
import PaginationComponent from "components/Pagination";


// components

export default function Addpelanggan() {
  const [pelanggan, setPelanggan] = React.useState([]);

  const [totalItems, setTotalItems] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [search, setSearch] = useState('');
  const [sorting, setSorting] = useState({ field: "", order: "" });
  const [loading, setLoading] = useState(false);

  const [pageSize, setPageSize] = useState(10);

  const [kode_pelanggan, setKode_pelanggan] = React.useState('')
  const [nama, setNama] = React.useState('')
  const [perusahaan, setPerusahaan] = React.useState('')
  const [telp, setTelp] = React.useState('')
  const [email, setEmail] = React.useState('')
  const [provinsi, setProvinsi] = React.useState('')
  const [kota, setKota] = React.useState('')
  const [kode_pos, setKode_pos] = React.useState('')
  const [tukar, setTukar] = useState('simpan')


  React.useEffect(() => {
    getpelanggan()
  }, [])

  const getpelanggan = () => {
    const options = { method: 'GET', url: 'https://afgan.hizraniaga.com/m_api.php', params: { a: 'pelanggan' } };

    axios.request(options).then(function (response) {
      console.log(response.data);
      setPelanggan(response.data.data)
    }).catch(function (error) {
      console.error(error);
    });
  }

  const postaddpelanggan = () => {
    const options = {
      method: 'POST',
      url: 'https://afgan.hizraniaga.com/m_api.php',
      params: { a: 'addpelanggan' },
      headers: { 'Content-Type': 'application/json' },
      data: {
        kode_pelanggan: kode_pelanggan,
        nama: nama,
        perusahaan: perusahaan,
        telp: telp,
        email: email,
        provinsi: provinsi,
        kota: kota,
        kode_pos: kode_pos
      }
    };

    axios.request(options).then(function (response) {
      console.log(response.data);
      if (response.data.Pesan === 'Berhasil') {
        setTukar('simpan')
        getpelanggan()
        setKode_pelanggan('')
        setNama('')
        setPerusahaan('')
        setTelp('')
        setEmail('')
        setProvinsi('')
        setKota('')
        setKode_pos('')
      }
    }).catch(function (error) {
      console.error(error);
    });
  }
const posteditdata = () => {
  const options = {
    method: 'POST',
    url: 'https://afgan.hizraniaga.com/m_api.php',
    params: {a: 'editpelanggan'},
    headers: {'Content-Type': 'application/json'},
    data: {
      kode_pelanggan: kode_pelanggan,
      nama: nama,
      perusahaan: perusahaan,
      telp: telp,
      email: email,
      provinsi: provinsi,
      kota: kota,
      kode_pos: kode_pos
    }
  };

  axios.request(options).then(function (response) {
    console.log(response.data);
    if (response.data.Pesan === 'Berhasil') {
      setTukar('simpan')
      getpelanggan()
      setKode_pelanggan('')
      setNama('')
      setPerusahaan('')
      setTelp('')
      setEmail('')
      setProvinsi('')
      setKota('')
      setKode_pos('')
    }
  }).catch(function (error) {
    console.error(error);
  });
}


  const onData = (item) => {
    setTukar('edit')
    setKode_pelanggan(item.kode_pelanggan)
    setNama(item.nama)
    setPerusahaan(item.perusahaan)
    setTelp(item.telp)
    setEmail(item.email)
    setProvinsi(item.provinsi)
    setKota(item.kota)
    setKode_pos(item.kode_pos)
    console.log(item)
  }
  const commentsData = useMemo(() => {
    // console.log(search)
    let computedComments = pelanggan;

    // if (searchQuery) {
    //   computedComments = computedComments.filter(
    //     comment =>
    //       comment.barang_nama.toLowerCase().includes(searchQuery.toLowerCase()) ||
    //       comment.barang_id.toLowerCase().includes(searchQuery.toLowerCase())
    //   );
    // }

    if (search) {
      computedComments = computedComments.filter(
        comment =>
          comment.kode_pelanggan.toLowerCase().includes(search.toLowerCase())

      );
    }

    setTotalItems(computedComments.length);

    //Sorting comments
    if (sorting.field) {
      const reversed = sorting.order === "asc" ? 1 : -1;
      computedComments = computedComments.sort(
        (a, b) =>
          reversed * a[sorting.field].localeCompare(b[sorting.field])
      );
    }

    //Current Page slice
    return computedComments.slice(
      (currentPage - 1) * pageSize,
      (currentPage - 1) * pageSize + pageSize
    );
  }, [pelanggan, currentPage, search, sorting]);

  return (
    <>
      <div className="relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded-lg bg-blueGray-100 border-0">
        <div className="rounded-t bg-white mb-0 px-6 py-6">
          <div className="text-center flex justify-between">
            <h6 className="text-blueGray-700 text-xl font-bold">Data Pelanggan</h6>

          </div>
        </div>
        <div className="flex-auto px-4 lg:px-10 py-10 pt-0">
          <form>
            <h6 className="text-blueGray-400 text-sm mt-3 mb-6 font-bold uppercase">
              Input Data Pelanggan
            </h6>
            <div className="flex flex-wrap">
              <div className="w-full lg:w-6/12 px-4">
                <div className="relative w-full mb-3">
                  <label
                    className="block uppercase text-blueGray-600 text-xs font-bold mb-2"
                    htmlFor="grid-password"
                  >
                    Kode Pelanggan
                  </label>
                  <input
                    value={kode_pelanggan}
                    onChange={(e) => { setKode_pelanggan(e.target.value); console.log(kode_pelanggan) }}
                    type="text"
                    className="border-0 px-3 py-3 placeholder-blueGray-300 text-blueGray-600 bg-white rounded text-sm shadow focus:outline-none focus:ring w-full ease-linear transition-all duration-150"
                    defaultValue=""
                  />
                </div>
              </div>
              <div className="w-full lg:w-6/12 px-4">
                <div className="relative w-full mb-3">
                  <label
                    className="block uppercase text-blueGray-600 text-xs font-bold mb-2"
                    htmlFor="grid-password"
                  >
                    Nama
                  </label>
                  <input
                    value={nama}
                    onChange={(e) => { setNama(e.target.value); console.log(nama) }}
                    type="text"
                    className="border-0 px-3 py-3 placeholder-blueGray-300 text-blueGray-600 bg-white rounded text-sm shadow focus:outline-none focus:ring w-full ease-linear transition-all duration-150"
                    defaultValue=""
                  />
                </div>
              </div>
              <div className="w-full lg:w-6/12 px-4">
                <div className="relative w-full mb-3">
                  <label
                    className="block uppercase text-blueGray-600 text-xs font-bold mb-2"
                    htmlFor="grid-password"
                  >
                    Perusahaan
                  </label>
                  <input
                    value={perusahaan}
                    onChange={(e) => { setPerusahaan(e.target.value); console.log(perusahaan) }}
                    type="text"
                    className="border-0 px-3 py-3 placeholder-blueGray-300 text-blueGray-600 bg-white rounded text-sm shadow focus:outline-none focus:ring w-full ease-linear transition-all duration-150"
                    defaultValue=""
                  />
                </div>
              </div>
              <div className="w-full lg:w-6/12 px-4">
                <div className="relative w-full mb-3">
                  <label
                    className="block uppercase text-blueGray-600 text-xs font-bold mb-2"
                    htmlFor="grid-password"
                  >
                    Telp
                  </label>
                  <input
                    value={telp}
                    onChange={(e) => { setTelp(e.target.value); console.log(telp) }}
