const initialState = {
    
    lapbulan: [],
    namaBulan : '',
    grandtotal:''
}

function lapbulanReducer(state = initialState, action) {
    switch (action.type) {
        case "LAP_BULAN":
            return {
                ...state,
                lapbulan: action.lapbulan,
               
            }
            case "NAMA_BULAN":
                return{
                    ...state,
                    namaBulan : action.namaBulan

                }
                case "GRAND_TOTAL":
                return{
                    ...state,
                    grandtotal : action.grandtotal

                }
        default:
            return state
    }
}

export default lapbulanReducer
