import React, { useEffect, useState, useMemo } from "react";
import axios from 'axios'
import PaginationComponent from "components/Pagination";
import Api from "api/Api";
import { useDispatch, useSelector } from "react-redux";
import { useHistory } from "react-router";

import moment from "moment";
// components

export default function LaporanHari() {
  const dispatch = useDispatch()
  const [barang, setBarang] = React.useState([]);
  const user = useSelector(state => state.userReducer.user)
  const [totalItems, setTotalItems] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [search, setSearch] = useState('');
  const [sorting, setSorting] = useState({ field: "", order: "" });
  const [loading, setLoading] = useState(false);
  const history = useHistory()
  const [pageSize, setPageSize] = useState(10);
  const [tanggal, setTanggal] = React.useState('')
  const [kode_barang, setKode_barang] = React.useState('')
  const [nama_barang, setNama_barang] = React.useState('')
  const [jenis, setJenis] = React.useState('')
  const [merek, setMerek] = React.useState('')
  const [kategori, setKategori] = React.useState('Baja Ringan')
  const [unit, setUnit] = React.useState('Lembar')
  const [harga, setHarga] = React.useState('')
  const [stok, setStok] = React.useState('')
  const [laba, setLaba] = useState(0)

  const [tukar, setTukar] = useState('simpan')

  React.useEffect(() => {
    getDataBarang()


  }, [])

  const getTot = () => {
    let totals = 0;
    let banyak = 0;
    barang.forEach(element => {
      banyak = banyak + parseInt((element.harga * element.qty) - (element.modal * element.qty));
    });
    // getDataPelanggan()
    // getDataTransaksi()
    setLaba(banyak)
    console.log('laba', banyak);
  }

  const getDataBarang = () => {
    let totals = 0;
    let banyak = 0;
    const options = {
      method: 'GET',
      url: Api.url,
      params: { a: 'lap_jual_harian', admin: user[0]?.username, hari: tanggal }
    };

    axios.request(options).then(function (response) {
      console.log(response.data);
      setBarang(response.data.data)

      response.data.data.forEach(element => {
        banyak = banyak + parseInt((element.harga * element.qty) - (element.modal * element.qty));
        totals = totals + parseInt(element.total);
      });

      // getDataPelanggan()
      // getDataTransaksi()
      setLaba(banyak)
      dispatch({
        type: "LAP_HARI",
        laphari: response.data.data
      })
      dispatch({
        type: "LABA_HARI",
        labahari: banyak
      })
      dispatch({
        type: "GRAND_TOTAL",
        grandtotal: totals
      })
      console.log('laba', banyak);
    }).catch(function (error) {
      console.error(error);
    });

  }

  function currencyFormat(num) {
    return 'Rp ' + parseFloat(num).toFixed().replace(/(\d)(?=(\d{3})+(?!\d))/g, '$1,');
  }

  // const getDataTransaksi = () => {
  //   const options = { method: 'GET', url: 'https://afgan.hizraniaga.com/m_api.php', params: { a: 'transaksi' } };

  //   axios.request(options).then(function (response) {
  //     console.log(response.data);
  //     setBarang(response.data.data)
  //   }).catch(function (error) {
  //     console.error(error);
  //   });
  // }

  const commentsData = useMemo(() => {
    // console.log(search)
    let computedComments = barang;

    // if (searchQuery) {
    //   computedComments = computedComments.filter(
    //     comment =>
    //       comment.barang_nama.toLowerCase().includes(searchQuery.toLowerCase()) ||
    //       comment.barang_id.toLowerCase().includes(searchQuery.toLowerCase())
    //   );
    // }

    if (tanggal) {
      computedComments = computedComments.filter(
        comment =>
          comment.tanggal.toLowerCase().includes(tanggal.toLowerCase())

      );
    }

    setTotalItems(computedComments.length);

    //Sorting comments
    if (sorting.field) {
      const reversed = sorting.order === "asc" ? 1 : -1;
      computedComments = computedComments.sort(
        (a, b) =>
          reversed * a[sorting.field].localeCompare(b[sorting.field])
      );
    }

    //Current Page slice
    return computedComments.slice(
      (currentPage - 1) * pageSize,
      (currentPage - 1) * pageSize + pageSize
    );
  }, [barang, currentPage, search, sorting]);

  const postBarang = () => {
    const options = {
      method: 'POST',
      url: 'https://afgan.hizraniaga.com/m_api.php/m_api.php',
      params: { a: 'addproduct' },
      headers: { 'Content-Type': 'application/json' },
      data: {
        kode_barang: kode_barang,
        nama_barang: nama_barang,
        jenis: jenis,
        merek: merek,
        kategori: kategori,
        unit: unit,
        harga: harga,
        stok: stok
      }
    };

    axios.request(options).then(function (response) {
      console.log(response.data);
      if (response.data.Pesan === 'Berhasil') {
        setTukar('simpan')
        getDataBarang()
        setKode_barang('')
        setNama_barang('')
        setKategori('')
        setUnit('')
        setMerek('')
        setJenis('')
        setHarga('')
        setStok('')
      }
    }).catch(function (error) {
      console.error(error);
    });
  }

  const editbarang = () => {
    const options = {
      method: 'POST',
      url: 'https://afgan.hizraniaga.com/m_api.php/m_api.php',
      params: { a: 'editproduct' },
      headers: { 'Content-Type': 'application/json' },
      data: {
        kode_barang: kode_barang,
        nama_barang: nama_barang,
        jenis: jenis,
        merek: merek,
        kategori: kategori,
        unit: unit,
        harga: harga,
        stok: stok
      }
    };

    axios.request(options).then(function (response) {
      console.log(response.data);
      if (response.data.Pesan === 'Berhasil') {
        setTukar('simpan')
        getDataBarang()
        setKode_barang('')
        setNama_barang('')
        setKategori('')
        setUnit('')
        setMerek('')
        setJenis('')
        setHarga('')
        setStok('')
      }
    }).catch(function (error) {
      console.error(error);
    });
  }

  const onData = (item) => {
    setTukar('edit')
    setKode_barang(item.kode_barang)
    setNama_barang(item.nama_barang)
    setKategori(item.kategori)
    setUnit(item.unit)
    setMerek(item.merek)
    setJenis(item.jenis)
    setHarga(item.harga)
    setStok(item.stok)
    console.log(item)
  }
  const ontanggal = (e) => {
    setTanggal(e)
    setCurrentPage(1)
    dispatch({
      type: "NAMA_HARI",
      namaHari: moment(e).format('DD-MM-YYYY')
    })
  }

  return (
    <>
      <div className="relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded-lg bg-blueGray-100 border-0">
        {/* <div className="rounded-t bg-white mb-0 px-6 py-6">
          <div className="text-center flex justify-between">
            <h6 className="text-blueGray-700 text-xl font-bold">Laporan Harian</h6>


          </div>

        </div> */}
        <div className="flex-auto px-4 lg:px-10 py-10 pt-0">
          <form>
            {/* <h6 className="text-blueGray-400 text-sm mt-3 mb-6 font-bold uppercase">
              Input Data Barang
            </h6>  */}
            {/* <div className="flex flex-wrap">
              <div className="w-full lg:w-6/12 px-4">
                <div className="relative w-full mb-3">
                  <label
                    className="block uppercase text-blueGray-600 text-xs font-bold mb-2"
                    htmlFor="grid-password"
                  >
                    Kode Barang
                  </label>
                  <input
                    value={kode_barang}
                    onChange={(e) => { setKode_barang(e.target.value); console.log(kode_barang) }}
                    type="text"
                    className="border-0 px-3 py-3 placeholder-blueGray-300 text-blueGray-600 bg-white rounded text-sm shadow focus:outline-none focus:ring w-full ease-linear transition-all duration-150"
                    defaultValue=""
                  />
                </div>
              </div>
              <div className="w-full lg:w-6/12 px-4">
                <div className="relative w-full mb-3">
                  <label
                    className="block uppercase text-blueGray-600 text-xs font-bold mb-2"
                    htmlFor="grid-password"
                  >
                    Nama Barang
                  </label>
                  <input
                    value={nama_barang}
                    onChange={(e) => { setNama_barang(e.target.value); console.log(nama_barang) }}
                    type="text"
                    className="border-0 px-3 py-3 placeholder-blueGray-300 text-blueGray-600 bg-white rounded text-sm shadow focus:outline-none focus:ring w-full ease-linear transition-all duration-150"
                    defaultValue=""
                  />
                </div>
              </div>
              <div className="w-full lg:w-6/12 px-4">
                <div className="relative w-full mb-3">
                  <label
                    className="block uppercase text-blueGray-600 text-xs font-bold mb-2"
                    htmlFor="grid-password"
                  >
                    Jenis
                  </label>
                  <input
                    value={jenis}
                    onChange={(e) => { setJenis(e.target.value); console.log(jenis) }}
